mapper.select:
/* com.lj.core.common.code.CodeMapper.selectCodeListForCodeHandler */ SELECT MST.CTGR_CD , MST.CD_NM AS CTGR_CD_NM , MST.USE_YN AS CTGR_USE_YN , DTL.DTL_CD , CNM.LANG_CD , CNM.CD_NM AS DTL_CD_NM , DTL.USE_YN AS DTL_USE_YN , DTL.CD_XPLN , DTL.ERP_STRD_CD , DTL.ADD_INFO1 , DTL.ADD_INFO2 , DTL.ADD_INFO3 , DTL.ST_SEQ FROM TB_CMM_CD_MST MST JOIN TB_CMM_CD_DTL DTL ON MST.CTGR_CD = DTL.CTGR_CD LEFT OUTER JOIN TB_CMM_CD_DTL_NM CNM ON DTL.CTGR_CD = CNM.CTGR_CD AND DTL.DTL_CD = CNM.DTL_CD
MST.CTGR_CD = #{ctgrCd}
ORDER BY MST.CTGR_CD ASC, DTL.ST_SEQ ASC, DTL.DTL_CD ASC
