mapper.insert:
 /* com.lj.crewpnr.mapper.pssdb.CrewBookingMapper.insertCrewPnrLog */


     SELECT NVL(MAX(SEQ), 0) + 1 AS seq FROM TB_CREW_PNR_LOG


INSERT INTO TB_CREW_PNR_LOG(
       SEQ
     , DEP_DATE
     , FLT_NUM
     , STNFR_CODE
     , STNTO_CODE
     , FARE_CLASS
     , PAX_CNT
     , ERROR_VALUE
     ,RGST_DTTM
 )
VALUES(
       #{seq}
     , #{depDate}
     , #{fltNum}
     , #{stnfrCode}
     , #{stntoCode}
     , #{fareClass}
     , #{paxCnt}
     , #{errorValue}
     , SYSDATE
 )

mapper.select:
/* com.lj.crewpnr.mapper.pssdb.CrewBookingMapper.getCreateBookingFailLog */ SELECT DEP_DATE, FLT_NUM, STNFR_CODE, STNTO_CODE, FARE_CLASS, PAX_CNT, ERROR_VALUE, RGST_DTTM FROM TB_CREW_PNR_LOG WHERE STNFR_CODE = #{stnfrCode} AND STNTO_CODE = #{stntoCode} AND DEP_DATE BETWEEN #{brdStrtDt} AND #{brdEndDt}
AND FARE_CLASS IN ('C', 'U0')
AND FARE_CLASS IN ('U1', 'U3')
AND PAX_CNT = #{paxCount}
ORDER BY RGST_DTTM DESC
